function client(endpoint, customConfig = {}) {
  const api = process.env.GENIUS_API_URL
  const config = {
    method: "GET",
    headers: {
            "Authorization": "Bearer" + process.env.GENIUS_API_CLIENT_TOKEN,
            "User-Agent": "CompuServe Classic/1.22",
            "Content-Type": "application/json",
        },
    ...customConfig,
  }

  return window
    .fetch(`${api}/${endpoint}`, config)
      .then(response => response.json())
}
/* 
GET /songs/:id
Data for a specific song.

GET /artists/:id
Data for a specific artist.

GET /artists/:id/songs
 */
export {client}
