function client(endpoint, customConfig = {}) {
  const config = {
    method: 'GET',
    withCredentials: true,
    credentials: 'include',
    headers: {
      'Authorization': `Bearer ${process.env.GENIUS_API_CLIENT_TOKEN}`,
      'Content-Type': 'application/json'
    }, 
    ...customConfig,
  }

  return window
    .fetch(`${process.env.GENIUS_API_URL}/${endpoint}`, config)
    .then(response => {
      response.json()
      console.log(response.json())
    })
}

export {client}
