const api = process.env.GENIUS_API_URL
const config = {
    method: "GET",
    /* headers: {
      "Authorization": "Bearer" + process.env.GENIUS_API_CLIENT_TOKEN,
      "Content-Type": "application/json; charset=utf-8",
      "Accept": "application/json" 
        }, */
  }

function FetchAsyncData() {
    const [data, setData] = React.useState(null)

    React.useEffect(() => {
        window.fetch(`${api}/search?q=Kendrick%20Lamar&access_token=${process.env.GENIUS_API_CLIENT_TOKEN}`, config)
                        .then(response => response.json())
                        .then(res => setData(res.data))
    },[])

    return data ? console.log(data) : 'no data fetch'
}

export {FetchAsyncData}
