function client(endpoint, customConfig = {}) {
  const config = {
    method: "GET",
    response_type: "code",
    headers: {
            "Authorization": "Bearer" + process.env.GENIUS_API_CLIENT_TOKEN,
            "User-Agent": "CompuServe Classic/1.22",
            "Host": "api.genius.com",
            "Content-Type": "application/json",
        },
    ...customConfig,
  }

  return window
    .fetch(`${process.env.GENIUS_API_URL}/${endpoint}`, config)
      .then(response => response)
      .then(resData => console.log(resData))

}
/* 
GET /songs/:id
Data for a specific song.

GET /artists/:id
Data for a specific artist.

GET /artists/:id/songs
 */
export {client}
