/** @jsx jsx */
import {jsx} from '@emotion/core'

import * as React from 'react'
import Tooltip from '@reach/tooltip'
import {FaSearch, FaTimes} from 'react-icons/fa'
import {Input, BookListUL, Spinner} from '../lib'
import {BookRow} from '../book-row'
import {client} from '../../utils/api-client'
import * as colors from '../../styles/colors'
import { useAsync } from '../../utils/hooks'

function DiscoverSongOrArtistScreen() {
  const {data, error, run, isLoading, isSuccess, isError} = useAsync()
  const [query, setQuery] = React.useState()
  const [queried, setQueried] = React.useState(false)

  React.useEffect(() => {
    if (!queried) {
      return
    }
    run(client(`search?q=${encodeURIComponent(query)}`))
  }, [query, queried, run])

  function handleSearchSubmit(event) {
    event.preventDefault()
    setQueried(true)
    setQuery(event.target.elements.search.value)
  }

  return (
    <div
      css={{maxWidth: 800, margin: 'auto', width: '90vw', padding: '40px 0'}}
    >
      <form onSubmit={handleSearchSubmit}>
        <Input
          placeholder="Search songs or artists..."
          id="search"
          css={{width: '100%'}}
          genius-api-path-input-type='text'
          ng-model='path'
          ng-disabled='unauthorized'
        />
        <Tooltip label="Search Songs or Artists">
          <label htmlFor="search">
            <button
              type="submit"
              css={{
                border: '0',
                position: 'relative',
                marginLeft: '-35px',
                background: 'transparent',
              }}
              ng-click='update()'
              ng-hide='unauthorized'
            >
              {isLoading ? (
                <Spinner />
              ) : isError ? (
                <FaTimes aria-label="error" css={{color: colors.danger}} />
              ) : (
                <FaSearch aria-label="search" />
              )}
            </button>
          </label>
        </Tooltip>
      </form>

      {isError ? (
        <div css={{color: colors.danger}}>
          <p>There was an error:</p>
          <pre>{error.message}</pre>
        </div>
      ) : null}

      {isSuccess ? (
        data?.books?.length ? (
          <BookListUL css={{marginTop: 20}}>
            {data.books.map(book => (
              <li key={book.id} aria-label={book.title}>
                <BookRow key={book.id} book={book} />
              </li>
            ))}
          </BookListUL>
        ) : (
          <p>No books found. Try another search.</p>
        )
      ) : null}
    </div>
  )
}

export {DiscoverSongOrArtistScreen}
