import axios from 'axios'
function client(endpoint, customConfig = {}) {
  const api = process.env.GENIUS_API_URL
  const config = {
    method: "GET",
    /* headers: {
      "Authorization": "Bearer" + process.env.GENIUS_API_CLIENT_TOKEN,
      "Content-Type": "application/json; charset=utf-8",
      "Accept": "application/json" 
        }, */
    ...customConfig,
  }

  return window
    .fetch(encodeURIComponent(`${api}/${endpoint}&access_token=${process.env.GENIUS_API_CLIENT_TOKEN}`), config)
      .then(response => console.log(response))
}
/* 
GET /songs/:id
Data for a specific song.

GET /artists/:id
Data for a specific artist.

GET /artists/:id/songs
 */
export {client}
